                value: 1
              },{
                name: '下拉数据2',
                value: 2
              },{
                name: '下拉数据3',
                value: 3
              }]
            },
            {
              label: '输入测试',
              type: 'input', // 输入类型
              prop: 'cc',
              value: ''
            },
          ]
        }
      }
    },

    methods: {
      // 搜索回调方法
