      ref="video"
      id="videoCamera"
      autoPlay
      muted
      :width="videoWidth"
      :height="videoHeight"
      playsInline>
    </video>
    <canvas 
      style="display: none"
      :width="videoWidth"
      :height="videoHeight"
      id="canvasCamera">
    </canvas>
