      <!-->插槽可自定义自己的遮罩层 <!-->
      <slot name="mark"></slot>
    </div>
  </div>
</template>

<script>
import { Toast } from 'vant';

export default {
  props: {
    // 设置video 的宽
    videoWidth: {
      type: [ Number, String],
      default: 300
    },
    // 设置video 的高
    videoHeight: {
      type: [ Number, String],
      default: 300
    }
  },

  mounted() {
    // 进入页面 自动调用摄像头
    this.initCamera();
  },

  methods: {
    async initCamera() {
      // 提示
      await this.initToast('初始化...', 0)
      // 校验兼容性
      await this.checkCompatible()
      // 创建video
      await this.createVideo()

    },

    // 生成video
